<template>
    <div class="customdroparea">
      <div id="dropArea">
        <span class="droparea">Drop files here or <a href="#" 
          id="browse"><u>Browse</u></a></span>
        <ejs-uploader
          :asyncSettings="asyncSettings"
          :allowedExtensions="'.png'"
          ref="uploadObj"
          :dropArea="'#dropArea'"
          :template="'fileTemplate'"
          :progress="onProgress"
          :success="onSuccess"
          :failure="onFailure">
          <template #fileTemplate="{ data }">
            <div class="container">
              <span class="wrapper">
                <span :class="['icon sf-icon-' + data.type]"></span>
                <span class="file-name">{{ data.name }} ({{ data.size }} bytes)</span>
                <span class="upload-status"></span>
                <span class="e-icons e-file-remove-btn" title="Remove"></span>
              </span>
            </div>
          </template>
        </ejs-uploader>
      </div>
    </div>
</template>

<script>
import { UploaderComponent } from '@syncfusion/ej2-vue-inputs';
import { detach } from '@syncfusion/ej2-base';

export default {
  name: 'App',
  components: {
    'ejs-uploader': UploaderComponent
  },
  data() {
    return {
      asyncSettings: {
        saveUrl: 'https://services.syncfusion.com/vue/production/api/FileUploader/Save',
        removeUrl: 'https://services.syncfusion.com/vue/production/api/FileUploader/Remove',
      }
    };
  },
  mounted(){
    document.getElementById('browse').onclick = () => {
      document.querySelector('.e-file-select-wrap button').click();
    };

    document.getElementById('dropArea').onclick = (args) =>{
      if(args.target.classList.contains('e-file-delete-btn')){
        this.$refs.uploadObj.getFilesData().forEach((file)=>{
          if(file.name === args.target.closest('li').getAttribute('data-file-name')){
            this.$refs.uploadObj.remove(file);
          }
        });
      } else if( args.target.classList.contains('e-file-remove-btn')){
        detach(args.target.closest('li'));
      }
    };
  },
  methods:{
    onProgress(args){
      const progressValue = Math.round((args.e.loaded/args.e.total)*100) + '%';
      const li = this.getLiElement(args);
      li.querySelector('.upload-status').innerHTML = `${args.file.status} (${progressValue})`;
    },
    getLiElement(args){
      return document.querySelector('.e-upload-files').querySelector(`[data-file-name="${args.file.name}"]`);
    },
    onSuccess(args){
      const li = this.getLiElement(args);
      li.querySelector('.upload-status').innerHTML = args.file.status;
      li.querySelector('.upload-status').classList.add('upload-success');
    },
    onFailure(args){
      const li = this.getLiElement(args);
      li.querySelector('.upload-status').innerHTML = args.file.status;
      li.querySelector('.upload-status').classList.add('upload-failed');
    }
  }
};
</script>

