  padding: 6% 27% 6% 22%;
  display: block;
  border: 1px dashed #c3c3cc;
}

.customdroparea .e-file-select-wrap {
  display: none;
}

.customdroparea .e-upload {
  border:none;
}

.customdroparea .e-upload .e-upload-files {
  border-top: none;
}
.customdroparea .e-upload .e-upload-files .e-upload-file-list
{
  min-height: 65px;
